import React, { useState } from 'react';

const planets = [
  {
    name: 'Меркурий',
    radius: 2439.7,
    distanceFromSun: 57.91,
    moons: 0,
    color: '#b78727'
  },
  {
    name: 'Венера',
    radius: 6051.8,
    distanceFromSun: 108.2,
    moons: 0,
    color: '#f5d76e'
  },
  {
    name: 'Земля',
    radius: 6371,
    distanceFromSun: 149.6,
    moons: 1,
    color: '#4da6ff'
  },
  // ... другие планеты
];

const PlanetCard = ({ planet }) => (
  <div style={{ backgroundColor: planet.color, padding: '1rem', borderRadius: '0.5rem' }}>
    <h2>{planet.name}</h2>
    <p>Радиус: {planet.radius} км</p>
    <p>Удаленность от Солнца: {planet.distanceFromSun} млн км</p>
    <p>Количество спутников: {planet.moons}</p>
  </div>
);

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredPlanets = planets.filter((planet) =>
    planet.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <input
        type="text"
        placeholder="Поиск планет..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div>
        {filteredPlanets.map((planet, index) => (
          <PlanetCard key={index} planet={planet} />
        ))}
      </div>
    </div>
  );
};

export default App;
