import React, { useState } from 'react';
import './PlanetList.css';

const PlanetList = () => {
  const [planets, setPlanets] = useState([
    {
      id: 1,
      name: 'Меркурий',
      radius: '2,439 км',
      distance: '57,9 млн км',
      moons: '0',
    },
    {
      id: 2,
      name: 'Венера',
      radius: '6,051 км',
      distance: '108,2 млн км',
      moons: '0',
    },
    {
      id: 3,
      name: 'Земля',
      radius: '6,371 км',
      distance: '149,6 млн км',
      moons: '1 (Луна)',
    },
    {
      id: 4,
      name: 'Марс',
      radius: '3,389 км',
      distance: '227,9 млн км',
      moons: '2 (Фобос и Деймос)',
    },
    {
      id: 5,
      name: 'Юпитер',
      radius: '69,911 км',
      distance: '778,5 млн км',
      moons: '80 (крупнейшие: Ио, Европа, Ганимед, Каллисто)',
    },
  ]);



 const [searchTerm, setSearchTerm] = useState('');
  const [radiusFilter, setRadiusFilter] = useState('all');

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleRadiusFilter = (event) => {
    setRadiusFilter(event.target.value);
  };

  const filteredPlanets = planets.filter((planet) => {
    const radiusInRange =
      radiusFilter === 'all' ||
      (radiusFilter === '1000-5000' && planet.radius >= 1000 && planet.radius < 5000) ||
      (radiusFilter === '5000-10000' && planet.radius >= 5000 && planet.radius < 10000) ||
      (radiusFilter === '>10000' && planet.radius >= 10000);

    return (
      planet.name.toLowerCase().includes(searchTerm.toLowerCase()) && radiusInRange
    );
  });

  return (
    <div className="planet-list">
      <input
        type="text"
        placeholder="Поиск планет..."
        value={searchTerm}
        onChange={handleSearch}
        className="search-input"
      />
      <select value={radiusFilter} onChange={handleRadiusFilter}>
        <option value="all">Все радиусы</option>
        <option value="1000-5000">1000-5000 км</option>
        <option value="5000-10000">5000-10000 км</option>
        <option value=">10000">Более 10000 км</option>
      </select>
      <div className="planet-cards">
        {filteredPlanets.map((planet) => (
          <div key={planet.id} className="planet-card">
            <div className="planet-card-header">
              <h3>{planet.name}</h3>
            </div>
            <div className="planet-info">
              <p>
                <span className="info-label">Радиус:</span> {planet.radius} км
              </p>
              <p>
                <span className="info-label">Удаленность от Солнца:</span> {planet.distance}
              </p>
              <p>
                <span className="info-label">Спутники:</span> {planet.moons}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlanetList;
