import React, { useState, useEffect } from 'react';
import { Container, Row, Col, ListGroup, Card, FormControl, InputGroup } from 'react-bootstrap';

const PlanetsApp = () => {
  const [planets, setPlanets] = useState([]);
  const [selectedPlanet, setSelectedPlanet] = useState(null);
  const [planetDetails, setPlanetDetails] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchPlanets = async () => {
      try {
        const response = await fetch('https://api.le-systeme-solaire.net/rest.php/bodies');
        const data = await response.json();
        setPlanets(data.bodies.filter(body => body.isPlanet));
      } catch (error) {
        console.error('Ошибка при получении данных о планетах:', error);
      }
    };

    fetchPlanets();
  }, []);

  useEffect(() => {
    const fetchPlanetDetails = async () => {
      if (selectedPlanet) {
        try {
          const response = await fetch(`https://api.le-systeme-solaire.net/rest.php/bodies/${selectedPlanet.id}`);
          const data = await response.json();
          setPlanetDetails(data);
        } catch (error) {
          console.error('Ошибка при получении деталей планеты:', error);
        }
      } else {
        setPlanetDetails(null);
      }
    };

    fetchPlanetDetails();
  }, [selectedPlanet]);

  const handlePlanetClick = (planet) => {
    setSelectedPlanet(planet);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const filteredPlanets = planets.filter(planet =>
    planet.englishName.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <Container>
      <Row className="my-4">
        <Col md={4}>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Введите название планеты"
              value={searchQuery}
              onChange={handleSearchChange}
            />
          </InputGroup>
          <ListGroup>
            {filteredPlanets.map(planet => (
              <ListGroup.Item
                key={planet.id}
                onClick={() => handlePlanetClick(planet)}
                active={selectedPlanet === planet}
              >
                {planet.englishName}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
        <Col md={8}>
          {planetDetails && (
            <Card>
              <Card.Header>
                <Card.Title>{planetDetails.englishName}</Card.Title>
              </Card.Header>
              <Card.Body>
                <p>Масса: {planetDetails.mass.massValue * 10 ** planetDetails.mass.massExponent} кг</p>
                <p>Объем: {planetDetails.vol.volValue * 10 ** planetDetails.vol.volExponent} км³</p>
                <p>Плотность: {planetDetails.density} г/см³</p>
                <p>Гравитация: {planetDetails.gravity} м/с²</p>
                <p>Средний радиус: {planetDetails.meanRadius} км</p>
                {/* Добавьте другие свойства планеты по желанию */}
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default PlanetsApp;
