import React, { useState } from 'react';
import './PlanetList.css';

const PlanetList = () => {
  const [planets, setPlanets] = useState([
    {
      id: 1,
      name: 'Меркурий',
      radius: '2,439 км',
      distance: '57,9 млн км',
      moons: '0',
    },
    {
      id: 2,
      name: 'Венера',
      radius: '6,051 км',
      distance: '108,2 млн км',
      moons: '0',
    },
    {
      id: 3,
      name: 'Земля',
      radius: '6,371 км',
      distance: '149,6 млн км',
      moons: '1 (Луна)',
    },
    {
      id: 4,
      name: 'Марс',
      radius: '3,389 км',
      distance: '227,9 млн км',
      moons: '2 (Фобос и Деймос)',
    },
    {
      id: 5,
      name: 'Юпитер',
      radius: '69,911 км',
      distance: '778,5 млн км',
      moons: '80 (крупнейшие: Ио, Европа, Ганимед, Каллисто)',
    },
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredPlanets = planets.filter((planet) =>
    planet.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <input
        type="text"
        placeholder="Поиск планет..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div>
        {filteredPlanets.map((planet) => (
          <div key={planet.id} className="planet-card">
            <h3>{planet.name}</h3>
            <div className="planet-info">
              <p>Радиус: {planet.radius}</p>
              <p>Удаленность от Солнца: {planet.distance}</p>
              <p>Спутники: {planet.moons}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlanetList;
