import React, { useState } from 'react';

const PlanetList = () => {
  const [planets, setPlanets] = useState([
    {
      id: 1,
      name: 'Меркурий',
      description: 'Радиус: 2,439 км. Удаленность от Солнца: 57,9 млн км. Спутники: 0.',
    },
    {
      id: 2,
      name: 'Венера',
      description: 'Радиус: 6,051 км. Удаленность от Солнца: 108,2 млн км. Спутники: 0.',
    },
    {
      id: 3,
      name: 'Земля',
      description: 'Радиус: 6,371 км. Удаленность от Солнца: 149,6 млн км. Спутники: 1 (Луна).',
    },
    {
      id: 4,
      name: 'Марс',
      description: 'Радиус: 3,389 км. Удаленность от Солнца: 227,9 млн км. Спутники: 2 (Фобос и Деймос).',
    },
    {
      id: 5,
      name: 'Юпитер',
      description: 'Радиус: 69,911 км. Удаленность от Солнца: 778,5 млн км. Спутники: 80 (крупнейшие: Ио, Европа, Ганимед, Каллисто).',
    },
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredPlanets = planets.filter((planet) =>
    planet.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <input
        type="text"
        placeholder="Поиск планет..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div>
        {filteredPlanets.map((planet) => (
          <div key={planet.id}>
            <h3>{planet.name}</h3>
            <p>{planet.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlanetList;
