import React, { useState, useEffect } from 'react';

const PlanetsApp = () => {
  const [planets, setPlanets] = useState([]);

  useEffect(() => {
    const fetchPlanets = async () => {
      try {
        const response = await fetch('https://api.le-systeme-solaire.net/rest.php/bodies');
        const data = await response.json();
        setPlanets(data.bodies.filter(body => body.isPlanet));
      } catch (error) {
        console.error('Ошибка при получении данных о планетах:', error);
      }
    };

    fetchPlanets();
  }, []);

  return (
    <div>
      <h1>Планеты и их спутники</h1>
      {planets.map(planet => (
        <div key={planet.id}>
          <h2>{planet.englishName}</h2>
          {planet.moons && planet.moons.length > 0 ? (
            <div>
              <h3>Спутники:</h3>
              <ul>
                {planet.moons.map(moon => (
                  <li key={moon.moon}>{moon.moon}</li>
                ))}
              </ul>
            </div>
          ) : (
            <p>У этой планеты нет спутников.</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default PlanetsApp;
