import React, { useState, useEffect } from 'react';
import { Container, Row, Col, ListGroup, Card, FormControl, InputGroup, Button } from 'react-bootstrap';

const PlanetsApp = () => {
  const [bodies, setBodies] = useState([]);
  const [planets, setPlanets] = useState([]);
  const [selectedBody, setSelectedBody] = useState(null);
  const [bodyDetails, setBodyDetails] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredBodies, setFilteredBodies] = useState([]);
  const [showOnlyPlanets, setShowOnlyPlanets] = useState(false);

  useEffect(() => {
    const fetchBodies = async () => {
      try {
        const response = await fetch('https://api.le-systeme-solaire.net/rest.php/bodies');
        const data = await response.json();
        const allBodies = data.bodies;
        const planetBodies = allBodies.filter(body => body.isPlanet);
        setBodies(allBodies);
        setPlanets(planetBodies);
        setFilteredBodies(planetBodies); // по умолчанию отображаем планеты
      } catch (error) {
        console.error('Ошибка при получении данных о небесных телах:', error);
      }
    };

    fetchBodies();
  }, []);

  useEffect(() => {
    const fetchBodyDetails = async () => {
      if (selectedBody) {
        try {
          const response = await fetch(`https://api.le-systeme-solaire.net/rest.php/bodies/${selectedBody.id}`);
          const data = await response.json();
          setBodyDetails(data);
        } catch (error) {
          console.error('Ошибка при получении деталей небесного тела:', error);
        }
      } else {
        setBodyDetails(null);
      }
    };

    fetchBodyDetails();
  }, [selectedBody]);

  const handleBodyClick = (body) => {
    setSelectedBody(body);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearch = () => {
    if (searchQuery.trim() === '') {
      setFilteredBodies(showOnlyPlanets ? planets : bodies);
    } else {
      const filtered = bodies.filter(body =>
        body.englishName.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredBodies(showOnlyPlanets ? filtered.filter(body => body.isPlanet) : filtered);
    }
  };

  const handleReset = () => {
    setSearchQuery('');
    setFilteredBodies(showOnlyPlanets ? planets : bodies);
  };

  const handleTogglePlanets = () => {
    setShowOnlyPlanets(!showOnlyPlanets);
    setSearchQuery('');
    if (!showOnlyPlanets) {
      const filtered = bodies.filter(body =>
        body.englishName.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredBodies(filtered.filter(body => body.isPlanet));
    } else {
      setFilteredBodies(planets);
    }
  };

  return (
    <Container>
      <Row className="my-4">
        <Col md={4}>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Введите название небесного тела"
              value={searchQuery}
              onChange={handleSearchChange}
            />
            <Button variant="primary" onClick={handleSearch}>Поиск</Button>
            <Button variant="secondary" onClick={handleReset}>Сброс</Button>
          </InputGroup>
          <InputGroup className="mb-3">
            <InputGroup.Checkbox
              checked={showOnlyPlanets}
              onChange={handleTogglePlanets}
            />
            <InputGroup.Text>Только планеты</InputGroup.Text>
          </InputGroup>
          <ListGroup>
            {filteredBodies.map(body => (
              <ListGroup.Item
                key={body.id}
                onClick={() => handleBodyClick(body)}
                active={selectedBody === body}
              >
                {body.englishName}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
        <Col md={8}>
          {bodyDetails && (
            <Card>
              <Card.Header>
                <Card.Title>{bodyDetails.englishName}</Card.Title>
              </Card.Header>
              <Card.Body>
                <img
                  src={`/images/${bodyDetails.englishName}.jpeg`}
                  alt={bodyDetails.englishName}
                  onError={(e) => { e.target.onerror = null; e.target.src="/images/default.jpeg"; }} // Показать изображение по умолчанию, если не найдено
                  style={{ width: '100%', height: 'auto', marginBottom: '20px' }}
                />
                <p>Масса: {bodyDetails.mass?.massValue * 10 ** bodyDetails.mass?.massExponent} кг</p>
                <p>Объем: {bodyDetails.vol?.volValue * 10 ** bodyDetails.vol?.volExponent} км³</p>
                <p>Плотность: {bodyDetails.density} г/см³</p>
                <p>Гравитация: {bodyDetails.gravity} м/с²</p>
                <p>Средний радиус: {bodyDetails.meanRadius} км</p>
                <p>Тип небесного тела: {bodyDetails.bodyType}</p>
                {/* Добавьте другие свойства небесного тела по желанию */}
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default PlanetsApp;
