import React, { useState, useEffect } from 'react';
import { Container, Row, Col, ListGroup, Card, FormControl, InputGroup, Button } from 'react-bootstrap';
import './styles.scss';

const PlanetsApp = () => {
  const [bodies, setBodies] = useState([]);
  const [selectedBody, setSelectedBody] = useState(null);
  const [bodyDetails, setBodyDetails] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredBodies, setFilteredBodies] = useState([]);
  const [filterCategory, setFilterCategory] = useState('all');
  const [filterByRadius, setFilterByRadius] = useState('');

  useEffect(() => {
    const fetchBodies = async () => {
      try {
        const response = await fetch('https://api.le-systeme-solaire.net/rest.php/bodies');
        const data = await response.json();
        const allBodies = data.bodies.filter(body => body.meanRadius > 10);
        setBodies(allBodies);
        setFilteredBodies(allBodies); // Initially display all bodies
      } catch (error) {
        console.error('Error fetching celestial bodies data:', error);
      }
    };

    fetchBodies();
  }, []);

  useEffect(() => {
    const fetchBodyDetails = async () => {
      if (selectedBody) {
        try {
          const response = await fetch(`https://api.le-systeme-solaire.net/rest.php/bodies/${selectedBody.id}`);
          const data = await response.json();
          setBodyDetails(data);
        } catch (error) {
          console.error('Error fetching body details:', error);
        }
      } else {
        setBodyDetails(null);
      }
    };

    fetchBodyDetails();
  }, [selectedBody]);

  const handleBodyClick = (body) => {
    setSelectedBody(body);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearch = () => {
    let filtered = bodies.filter(body => body.englishName.toLowerCase().includes(searchQuery.toLowerCase()));
    filtered = filterBodies(filtered);
    setFilteredBodies(filtered);
  };

  const handleReset = () => {
    setSearchQuery('');
    setFilteredBodies(filterBodies(bodies));
  };

  const handleCategoryChange = (category) => {
    setFilterCategory(category);
    setFilteredBodies(filterBodies(bodies, category));
  };

  const handleRadiusChange = (event) => {
    setFilterByRadius(event.target.value);
    setFilteredBodies(filterBodies(bodies));
  };

  const filterBodies = (bodiesToFilter, category = filterCategory) => {
    return bodiesToFilter.filter(body => {
      if (category === 'planets' && !body.isPlanet) return false;
      if (category === 'moons' && body.bodyType !== 'Moon') return false;
      if (category === 'stars' && body.bodyType !== 'Star') return false;
      if (filterByRadius && body.meanRadius < parseFloat(filterByRadius)) return false;
      return true;
    });
  };

  const imageSources = {
    "Mercury": "https://example.com/mercury.jpg",
    "Venus": "https://example.com/venus.jpg",
    "Earth": "https://example.com/earth.jpg",
    "Mars": "https://example.com/mars.jpg",
    "Jupiter": "https://example.com/jupiter.jpg",
    "Saturn": "https://example.com/saturn.jpg",
    "Uranus": "https://example.com/uranus.jpg",
    "Neptune": "https://example.com/neptune.jpg",
    // Add more image URLs for other celestial bodies
  };

  return (
    <Container>
      <div className="galaxy-animation"></div>
      <Row className="my-4">
        <Col md={4}>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Enter celestial body name"
              value={searchQuery}
              onChange={handleSearchChange}
            />
            <Button variant="primary" onClick={handleSearch}>Search</Button>
            <Button variant="secondary" onClick={handleReset}>Reset</Button>
          </InputGroup>
          <ListGroup>
            {filteredBodies.map(body => (
              <ListGroup.Item
                key={body.id}
                onClick={() => handleBodyClick(body)}
                active={selectedBody === body}
              >
                {body.englishName}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
        <Col md={4}>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Minimum radius (km)"
              value={filterByRadius}
              onChange={handleRadiusChange}
              type="number"
            />
          </InputGroup>
          <Button variant="primary" onClick={() => handleCategoryChange('all')} className="me-2">
            All Objects
          </Button>
          <Button variant="primary" onClick={() => handleCategoryChange('planets')} className="me-2">
            Planets
          </Button>
          <Button variant="primary" onClick={() => handleCategoryChange('moons')} className="me-2">
            Moons
          </Button>
          <Button variant="primary" onClick={() => handleCategoryChange('stars')}>
            Stars
          </Button>
        </Col>
        <Col md={4}>
          {bodyDetails && (
            <Card>
              <Card.Header>
                <Card.Title>{bodyDetails.englishName}</Card.Title>
              </Card.Header>
              <Card.Body>
                <div className="img-container">
                  <img
                    src={imageSources[bodyDetails.englishName] || 'https://example.com/default.jpg'}
                    alt={bodyDetails.englishName}
                    onError={(e) => { e.target.onerror = null; e.target.src = "https://example.com/default.jpg"; }}
                  />
                </div>
                <p>Mass: {bodyDetails.mass?.massValue * 10 ** bodyDetails.mass?.massExponent} kg</p>
                <p>Volume: {bodyDetails.vol?.volValue * 10 ** bodyDetails.vol?.volExponent} km³</p>
                <p>Density: {bodyDetails.density} g/cm³</p>
                <p>Gravity: {bodyDetails.gravity} m/s²</p>
                <p>Mean radius: {bodyDetails.meanRadius} km</p>
                <p>Body type: {bodyDetails.bodyType}</p>
                {/* Add other properties as desired */}
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default PlanetsApp;
