import React, { useState } from 'react';

const CardList = () => {
  const [cards, setCards] = useState([
    { id: 1, name: 'Card 1', description: 'This is card 1' },
    { id: 2, name: 'Card 2', description: 'This is card 2' },
    { id: 3, name: 'Another Card', description: 'This is another card' },
    { id: 4, name: 'Yet Another Card', description: 'This is yet another card' },
    { id: 5, name: 'One More Card', description: 'This is one more card' },
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredCards = cards.filter((card) =>
    card.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <input
        type="text"
        placeholder="Search cards..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div>
        {filteredCards.map((card) => (
          <div key={card.id}>
            <h3>{card.name}</h3>
            <p>{card.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CardList;
