import React, { useState } from 'react';
import './PlanetList.css';

const PlanetList = () => {
  const [planets, setPlanets] = useState([
    {
      id: 1,
      name: 'Меркурий',
      radius: '2,439 км',
      distance: '57,9 млн км',
      moons: '0',
      color: '#9f8170',
    },
    {
      id: 2,
      name: 'Венера',
      radius: '6,051 км',
      distance: '108,2 млн км',
      moons: '0',
      color: '#f7d9c4',
    },
    {
      id: 3,
      name: 'Земля',
      radius: '6,371 км',
      distance: '149,6 млн км',
      moons: '1 (Луна)',
      color: '#3f91e3',
    },
    {
      id: 4,
      name: 'Марс',
      radius: '3,389 км',
      distance: '227,9 млн км',
      moons: '2 (Фобос и Деймос)',
      color: '#e35d6a',
    },
    {
      id: 5,
      name: 'Юпитер',
      radius: '69,911 км',
      distance: '778,5 млн км',
      moons: '80 (крупнейшие: Ио, Европа, Ганимед, Каллисто)',
      color: '#f5c360',
    },
  ]);

const [searchTerm, setSearchTerm] = useState('');
  const [filteredPlanets, setFilteredPlanets] = useState(planets);

  useEffect(() => {
    const filtered = planets.filter((planet) =>
      planet.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredPlanets(filtered);
  }, [searchTerm, planets]);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <div className="planet-list">
      <div className="stars"></div>
      <div className="twinkling"></div>
      <input
        type="text"
        placeholder="Поиск планет..."
        value={searchTerm}
        onKeyUp={handleSearch}
        className="search-input"
      />
      <div className="planet-cards">
        {filteredPlanets.map((planet) => (
          <div key={planet.id} className="planet-card" style={{ backgroundColor: planet.color }}>
            <div className="planet-card-header">
              <h3>{planet.name}</h3>
            </div>
            <div className="planet-info">
              <p>
                <span className="info-label">Радиус:</span> {planet.radius}
              </p>
              <p>
                <span className="info-label">Удаленность от Солнца:</span> {planet.distance}
              </p>
              <p>
                <span className="info-label">Спутники:</span> {planet.moons}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlanetList;
